Dim=Dimensions[TList1];
For[k=0,k<=Dim,TList1[[i,2,1]]=Re[TMat[P[TList1[[i,1,1]],TList1[[i,1,2]]],s[TList1[[i,1,1]],TList1[[i,1,2]]]]];TList1[[i,2,2]]=Im[TMat[P[TList1[[i,1,1]],TList1[[i,1,2]]],s[TList1[[i,1,1]],TList1[[i,1,2]]]]];TList1[[i,2,1]]=Spectral[P[TList1[[i,1,1]],TList1[[i,1,2]]],s[TList1[[i,1,1]],TList1[[i,1,2]]]]]
Dim=Dimensions[TList2];
For[k=0,k<=Dim,TList2[[i,2,1]]=Re[TMat[TList2[[i,1,1]],TList2[[i,1,2]]]];TList2[[i,2,2]]=Im[TMat[TList2[[i,1,1]],TList2[[i,1,2]]]];TList2[[i,2,1]]=Spectral[TList2[[i,1,1]],TList2[[i,1,2]]]]
Export["~/Physics/Research/MS/Spectral.0.xml", {TList1,TList2}];

